# Regression: calculate Beta
import numpy as np
from scipy.stats import linregress
from scipy.stats.mstats import zscore


def calculate_beta(SP500_data, all_data):
    beta_list = []
    stock_num = len(all_data)

    SP500_data = np.array(SP500_data)
    all_data = np.array(all_data)
    x = SP500_data[0, :, 1]

    for i in range(stock_num):
        y = all_data[i, :, 2]
        (beta_coeff, intercept, rvalue, pvalue, stderr) = linregress(x, y)
        # print('The Beta Coeff is: %f' % beta_coeff)
        beta_list.append(beta_coeff)
    return beta_list


